import React, { useState, useEffect } from 'react';
import TinderCard from 'react-tinder-card';
import axios from 'axios';

const MovieSwiper = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    // Fetch initial set of movies
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/movies/random', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMovies(prevMovies => [...prevMovies, response.data]);
    } catch (error) {
      console.error('Error fetching movie:', error);
    }
  };

  const handleSwipe = async (direction, movieId) => {
    if (direction === 'right') {
      // User liked the movie
      try {
        const token = localStorage.getItem('token');
        await axios.post('http://localhost:5000/api/movies/like', 
          { movieId },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        console.log('Movie liked!');
      } catch (error) {
        console.error('Error liking movie:', error);
      }
    }

    // Fetch a new movie when the current one is swiped
    fetchMovies();
  };

  return (
    <div className="movie-swiper">
      {movies.map((movie, index) => (
        <TinderCard
          key={movie.id}
          onSwipe={(dir) => handleSwipe(dir, movie.id)}
          preventSwipe={['up', 'down']}
        >
          <div className="movie-card">
            <img src={movie.poster} alt={movie.title} />
            <h2>{movie.title}</h2>
            <p>{movie.description}</p>
            <p>Genre: {movie.genre}</p>
            <p>Rating: {movie.rating}</p>
            <p>Length: {movie.length}</p>
            <p>Starring: {movie.starring}</p>
          </div>
        </TinderCard>
      ))}
    </div>
  );
};

export default MovieSwiper;
