import React, { useState } from 'react';
import axios from 'axios';
import MovieSwiper from './MovieSwiper';
import Matches from './Matches';
import AddMovie from './AddMovie';
import AllMovies from './AllMovies';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [currentView, setCurrentView] = useState('swiper');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    try {
      const response = await axios.post('http://127.0.0.1:5000/api/auth/login', { username, password });
      localStorage.setItem('token', response.data.access_token);
      setIsLoggedIn(true);
    } catch (error) {
      console.error('Login failed:', error);
      setError('Login failed. Please check your credentials.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setCurrentView('swiper');
  };

  if (!isLoggedIn) {
    return (
      <div className="App">
        <h1>Movie Matcher</h1>
        {error && <p className="error">{error}</p>}
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button type="submit">Login</button>
        </form>
      </div>
    );
  }

  return (
    <div className="App">
      <h1>Movie Matcher</h1>
      <nav>
        <button onClick={() => setCurrentView('swiper')}>Swipe Movies</button>
        <button onClick={() => setCurrentView('matches')}>View Matches</button>
        <button onClick={() => setCurrentView('add')}>Add Movie</button>
        <button onClick={() => setCurrentView('all')}>All Movies</button>
        <button onClick={handleLogout}>Logout</button>
      </nav>
      {currentView === 'swiper' && <MovieSwiper />}
      {currentView === 'matches' && <Matches />}
      {currentView === 'add' && <AddMovie />}
      {currentView === 'all' && <AllMovies />}
    </div>
  );
}

export default App;
