import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './MovieSwiper.css';

const MovieSwiper = () => {
  const [currentMovie, setCurrentMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchMovie();
  }, []);

  const fetchMovie = async () => {
    setLoading(true);
    setError(null);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://127.0.0.1:5000/api/movies/random', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCurrentMovie(response.data);
    } catch (error) {
      console.error('Error fetching movie:', error);
      setError('Failed to fetch movie. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSwipe = async (liked) => {
    if (liked) {
      try {
        const token = localStorage.getItem('token');
        await axios.post('http://127.0.0.1:5000/api/movies/like', 
          { movieId: currentMovie.id },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        console.log('Movie liked!');
      } catch (error) {
        console.error('Error liking movie:', error);
      }
    }
    fetchMovie();
  };

  if (loading) {
    return <div className="loading">Loading movie...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!currentMovie) {
    return <div className="no-movies">No more movies available.</div>;
  }

  return (
    <div className="movie-swiper">
      <div className="movie-card">
        <img src={currentMovie.poster} alt={currentMovie.title} />
        <h2>{currentMovie.title}</h2>
        <p>{currentMovie.description}</p>
        <p>Genre: {currentMovie.genre}</p>
        <p>Rating: {currentMovie.rating}</p>
        <p>Length: {currentMovie.length}</p>
        <p>Starring: {currentMovie.starring}</p>
      </div>
      <div className="swipe-buttons">
        <button onClick={() => handleSwipe(false)} className="dislike">Dislike</button>
        <button onClick={() => handleSwipe(true)} className="like">Like</button>
      </div>
    </div>
  );
};

export default MovieSwiper;
